import React, { useState } from 'react';
import { ThemeSwitch, Button, BreadCrumb, Input, validateOne } from 'switch-themes';
import './App.css';

function App() {

  const [theme, setTheme] = useState('light');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    validateOne
  }

  return (
    <>
      <ThemeSwitch themeState={theme}>
        <Input type="text" value='' placeHolder='text' onChange={handleInputChange} />
        <Button className='hello' label={theme} handleClick={() => setTheme(theme === 'light' ? 'dark' : 'light')} />
        <BreadCrumb
          className='h'
          arr={[
            { display: 'profile', routeTo: '' },
            { display: 'change-password', routeTo: '' },
          ]} />
      </ThemeSwitch>
    </>
  );
}

export default App;
